import React, { useState, useEffect, useMemo } from "react";
import { motion, AnimatePresence } from "framer-motion";

// --- Currency formatter ---
const fmt = (n) => `${n} BBB Coins`;

// --- Catalog ---
const CATALOG = [
  { id: "1", name: "BBB Proto 1", price: 199, sizes: [7,8,9,10,11], image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?q=80&w=800&auto=format&fit=crop", tag: "Best Seller" },
  { id: "2", name: "BBB Elevate", price: 169, sizes: [7,8,9,10], image: "https://images.unsplash.com/photo-1542291021-7eec264c27ff?q=80&w=800&auto=format&fit=crop", tag: "New" },
  { id: "3", name: "BBB Luxe Court", price: 249, sizes: [8,9,10,11], image: "https://images.unsplash.com/photo-1519741497674-611481863552?q=80&w=800&auto=format&fit=crop", tag: "Limited" },
  { id: "4", name: "BBB Trainer X", price: 149, sizes: [9,10,11,12], image: "https://images.unsplash.com/photo-1525966222134-fcfa99b8ae77?q=80&w=800&auto=format&fit=crop", tag: "Performance" },
  { id: "5", name: "BBB Speedform", price: 139, sizes: [7,8,9,10], image: "https://images.unsplash.com/photo-1543508282-6319a3e2621f?q=80&w=800&auto=format&fit=crop", tag: "Lightweight" }
];

// --- Cart Hook ---
function useCart() {
  const [items, setItems] = useState(() => {
    try {
      const stored = localStorage.getItem("bbb_cart");
      return stored ? JSON.parse(stored) : [];
    } catch {
      return [];
    }
  });

  useEffect(() => {
    localStorage.setItem("bbb_cart", JSON.stringify(items));
  }, [items]);

  const totalQty = useMemo(() => items.reduce((sum, i) => sum + i.qty, 0), [items]);
  const subtotal = useMemo(() => items.reduce((sum, i) => sum + i.qty * i.price, 0), [items]);

  const add = (product, { size }) => {
    const key = `${product.id}-${size}`;
    setItems(prev => {
      const idx = prev.findIndex(p => p.key === key);
      if (idx >= 0) {
        const next = [...prev];
        next[idx] = { ...next[idx], qty: Math.min(10, next[idx].qty + 1) };
        return next;
      }
      return [...prev, { key, id: product.id, name: product.name, size, price: product.price, image: product.image, qty: 1 }];
    });
  };

  const remove = (key) => setItems(prev => prev.filter(p => p.key !== key));
  const setQty = (key, qty) => setItems(prev => prev.map(p => p.key === key ? { ...p, qty: Math.max(1, Math.min(10, qty)) } : p));
  const clear = () => setItems([]);

  return { items, totalQty, subtotal, add, remove, setQty, clear };
}

// --- NavBar ---
function NavBar({ cartCount, onCartClick }) {
  return (
    <header className="fixed top-0 w-full bg-white shadow z-50">
      <div className="max-w-7xl mx-auto px-4 py-3 flex items-center justify-between">
        <h1 className="text-xl font-bold">Elev8</h1>
        <button onClick={onCartClick} className="relative px-4 py-1 border rounded hover:shadow">
          Cart
          {cartCount > 0 && (
            <span className="absolute -top-2 -right-2 bg-black text-white text-xs px-2 py-0.5 rounded-full">
              {cartCount}
            </span>
          )}
        </button>
      </div>
    </header>
  );
}

// --- Hero ---
function Hero() {
  return (
    <section className="pt-24 bg-gradient-to-br from-zinc-900 via-black to-zinc-800 text-white">
      <div className="max-w-7xl mx-auto px-4 py-16 text-center">
        <h2 className="text-4xl font-bold mb-2">Elev8 Your Game</h2>
        <p className="text-lg text-zinc-300 mb-4">Fall '25 Collection</p>
        <a href="#shop" className="inline-block bg-white text-black px-6 py-2 rounded font-semibold hover:bg-zinc-200">
          Shop Now
        </a>
      </div>
    </section>
  );
}

// --- ProductCard ---
function ProductCard({ product, onAdd }) {
  const [size, setSize] = useState(product.sizes[0]);

  return (
    <div className="border rounded-lg overflow-hidden shadow hover:shadow-lg flex flex-col relative bg-white text-black">
      {product.tag && <span className="absolute top-2 left-2 bg-black text-white text-xs px-2 py-1 rounded-full">{product.tag}</span>}
      <img src={product.image} alt={product.name} className="h-40 w-full object-cover" />
      <div className="p-3 flex flex-col gap-2 flex-1">
        <h3 className="font-semibold text-sm">{product.name}</h3>
        <p className="font-bold text-sm">{fmt(product.price)}</p>
        <div className="flex gap-1 overflow-x-auto">
          {product.sizes.map(sz => (
            <button key={sz} onClick={() => setSize(sz)} className={`text-xs px-2 py-1 border rounded ${size === sz ? "bg-black text-white" : ""}`}>{sz}</button>
          ))}
        </div>
        <button onClick={() => onAdd(product, { size })} className="bg-black text-white text-sm rounded py-1 hover:bg-gray-900 mt-auto">
          Add to Cart
        </button>
      </div>
    </div>
  );
}

// --- Shop ---
function Shop({ onAdd }) {
  return (
    <section id="shop" className="py-12 bg-gray-100">
      <div className="max-w-7xl mx-auto px-4">
        <h2 className="text-xl font-bold mb-4">Shop the Collection</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-4">
          {CATALOG.map(p => <ProductCard key={p.id} product={p} onAdd={onAdd} />)}
        </div>
      </div>
    </section>
  );
}

// --- CartDrawer ---
function CartDrawer({ open, cart, subtotal, onClose, onRemove, onSetQty, onCheckout }) {
  return (
    <AnimatePresence>
      {open && (
        <>
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/30 z-40"
            onClick={onClose}
          />
          <motion.div
            initial={{ x: 400 }}
            animate={{ x: 0 }}
            exit={{ x: 400 }}
            transition={{ type: "spring", stiffness: 100 }}
            className="fixed top-0 right-0 w-80 h-full bg-white z-50 flex flex-col shadow-lg"
          >
            <div className="p-4 border-b flex justify-between items-center">
              <h2 className="font-bold text-lg">Your Cart</h2>
              <button onClick={onClose} className="text-xl font-bold">×</button>
            </div>
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {cart.length === 0 ? (
                <p className="text-gray-500 text-center">Your cart is empty.</p>
              ) : (
                cart.map(item => (
                  <div key={item.key} className="flex gap-3 items-center border-b pb-2">
                    <img src={item.image} alt={item.name} className="w-16 h-16 object-cover rounded" />
                    <div className="flex-1">
                      <h3 className="text-sm font-semibold">{item.name}</h3>
                      <p className="text-xs text-gray-500">Size: {item.size}</p>
                      <p className="text-sm font-bold">{fmt(item.price * item.qty)}</p>
                      <div className="flex items-center gap-2 mt-1">
                        <button onClick={() => onSetQty(item.key, item.qty - 1)} disabled={item.qty <= 1} className="px-2 border rounded text-sm">-</button>
                        <span>{item.qty}</span>
                        <button onClick={() => onSetQty(item.key, item.qty + 1)} disabled={item.qty >= 10} className="px-2 border rounded text-sm">+</button>
                      </div>
                    </div>
                    <button onClick={() => onRemove(item.key)} className="text-red-500 text-lg font-bold">×</button>
                  </div>
                ))
              )}
            </div>
            <div className="p-4 border-t">
              <p className="font-bold mb-2">Subtotal: {fmt(subtotal)}</p>
              <button onClick={onCheckout} className="w-full bg-black text-white py-2 rounded hover:bg-gray-900">
                Checkout
              </button>
            </div>
          </motion.div>
        </>
      )}
    </AnimatePresence>
  );
}

// --- Main App ---
export default function App() {
  const cart = useCart();
  const [cartOpen, setCartOpen] = useState(false);

  const handleCheckout = () => {
    alert("This is a demo. No real payment is processed.");
    cart.clear();
    setCartOpen(false);
  };

  return (
    <div className="bg-white text-black min-h-screen">
      <NavBar cartCount={cart.totalQty} onCartClick={() => setCartOpen(true)} />
      <Hero />
      <Shop onAdd={cart.add} />
      <CartDrawer
        open={cartOpen}
        cart={cart.items}
        subtotal={cart.subtotal}
        onClose={() => setCartOpen(false)}
        onRemove={cart.remove}
        onSetQty={cart.setQty}
        onCheckout={handleCheckout}
      />
    </div>
  );
}
