import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

function Navbar({ cart, subtotal, onCartOpen }) {
  return (
    <nav className="flex justify-between items-center px-8 py-4 bg-black text-white">
      {/* Logo */}
      <div className="text-2xl font-extrabold tracking-wider">ELEV8</div>

      {/* Menu */}
      <div className="flex gap-8">
        <a href="#shop" className="hover:text-gray-400">Shop</a>
        <a href="#about" className="hover:text-gray-400">About</a>
        <a href="#support" className="hover:text-gray-400">Support</a>
      </div>

      {/* Cart */}
      <button
        onClick={onCartOpen}
        className="bg-bbb-gold text-black px-4 py-2 rounded-lg hover:opacity-80 transition"
      >
        Cart ({cart.length}) - ${subtotal}
      </button>
    </nav>
  );
}

function CartDrawer({ cart, remove, setQty, clear, subtotal, open, onClose }) {
  return (
    <AnimatePresence>
      {open && (
        <motion.div
          initial={{ x: "100%" }}
          animate={{ x: 0 }}
          exit={{ x: "100%" }}
          transition={{ type: "tween" }}
          className="fixed top-0 right-0 w-80 max-w-sm h-full bg-white shadow-xl z-50 flex flex-col"
        >
          {/* Header */}
          <div className="flex justify-between items-center p-4 border-b">
            <h2 className="text-lg font-bold">Your Cart</h2>
            <button
              onClick={onClose}
              aria-label="Close cart"
              className="text-gray-600 hover:text-gray-800 text-xl"
            >
              ×
            </button>
          </div>

          {/* Content */}
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {cart.length === 0 ? (
              <p className="text-center text-gray-500 mt-8">
                Your cart is empty.
              </p>
            ) : (
              cart.map((item, idx) => (
                <div key={idx} className="flex gap-4 border-b pb-4">
                  <img
                    src={item.image}
                    alt={`${item.name} sneaker`}
                    className="w-20 h-20 object-cover rounded"
                  />
                  <div className="flex-1">
                    <h3 className="font-semibold">{item.name}</h3>
                    <p className="text-sm text-gray-500">Size {item.size}</p>
                    <p className="text-sm">${item.price}</p>
                    <div className="flex items-center gap-2 mt-1">
                      <button
                        onClick={() =>
                          setQty(item, Math.max(1, item.qty - 1))
                        }
                        aria-label="Decrease quantity"
                        className="px-2 py-1 border rounded"
                      >
                        -
                      </button>
                      <span>{item.qty}</span>
                      <button
                        onClick={() => setQty(item, item.qty + 1)}
                        aria-label="Increase quantity"
                        className="px-2 py-1 border rounded"
                      >
                        +
                      </button>
                    </div>
                    <button
                      onClick={() => remove(item)}
                      aria-label="Remove item"
                      className="text-red-500 text-sm mt-1"
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))
            )}
          </div>

          {/* Footer */}
          <div className="p-4 border-t">
            {cart.length > 0 ? (
              <>
                <p className="font-semibold mb-2">Subtotal: ${subtotal}</p>
                <button
                  onClick={clear}
                  className="w-full bg-black text-white py-2 rounded-lg hover:bg-gray-800 transition"
                >
                  Checkout
                </button>
              </>
            ) : (
              <button
                onClick={onClose}
                className="w-full bg-gray-200 text-gray-600 py-2 rounded-lg hover:bg-gray-300 transition"
              >
                Continue Shopping
              </button>
            )}
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

export default function App() {
  const [cart, setCart] = useState([]);
  const [open, setOpen] = useState(false);

  const subtotal = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

  const remove = (item) =>
    setCart(cart.filter((c) => !(c.name === item.name && c.size === item.size)));

  const setQty = (item, qty) =>
    setCart(
      cart.map((c) =>
        c.name === item.name && c.size === item.size ? { ...c, qty } : c
      )
    );

  const clear = () => setCart([]);

  return (
    <>
      <Navbar cart={cart} subtotal={subtotal} onCartOpen={() => setOpen(true)} />
      <CartDrawer
        cart={cart}
        remove={remove}
        setQty={setQty}
        clear={clear}
        subtotal={subtotal}
        open={open}
        onClose={() => setOpen(false)}
      />
      <main className="p-8">
        <h1 className="text-3xl font-bold">Welcome to ELEV8</h1>
        <p className="mt-2 text-gray-600">Streetwear inspired by champions.</p>
        {/* Example Add to Cart */}
        <button
          onClick={() =>
            setCart([
              ...cart,
              { name: "BBB Sneaker", size: 10, price: 200, qty: 1, image: "https://via.placeholder.com/150" },
            ])
          }
          className="mt-6 bg-bbb-gold text-black px-6 py-3 rounded-lg hover:opacity-80 transition"
        >
          Add Sneaker to Cart
        </button>
      </main>
    </>
  );
}
