// App.jsx
import React, { useEffect, useMemo, useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

// --- Helper: fake currency style ---
const fmt = (n) => `${n} BBB Coins`;

// --- Sample Catalog ---
const CATALOG = [
  { id: "bbb-proto-1", name: "BBB Proto 1", color: "Black/Gold", colors: ["Black/Gold", "White/Gold"], sizes: [7,8,9,10,11,12,13], price: 199, rating: 4.7, reviews: 162, image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?q=80&w=1200&auto=format&fit=crop", tag: "Best Seller", description: "Signature cushioning with a springy midsole and carbon plate pop. A court-ready profile tuned for all-day wear." },
  { id: "bbb-proto-2", name: "BBB Elevate", color: "White/Black", colors: ["White/Black", "Red/Black"], sizes: [6,7,8,9,10,11,12], price: 169, rating: 4.5, reviews: 94, image: "https://images.unsplash.com/photo-1542291021-7eec264c27ff?q=80&w=1200&auto=format&fit=crop", tag: "New", description: "Breathable knit upper with lockdown lacing. Lightweight, responsive, and built to move fast." },
  { id: "bbb-lux-1", name: "BBB Luxe Court", color: "Cream/Gold", colors: ["Cream/Gold", "Black/Black"], sizes: [7,8,9,10,11,12], price: 249, rating: 4.9, reviews: 58, image: "https://images.unsplash.com/photo-1519741497674-611481863552?q=80&w=1200&auto=format&fit=crop", tag: "Limited", description: "Premium leather, micro-perf details, and a smooth ride. Off-court elegance meets on-court heritage." },
  { id: "bbb-train-1", name: "BBB Trainer X", color: "Volt/Black", colors: ["Volt/Black", "Grey/White"], sizes: [8,9,10,11,12,13,14], price: 149, rating: 4.3, reviews: 203, image: "https://images.unsplash.com/photo-1525966222134-fcfa99b8ae77?q=80&w=1200&auto=format&fit=crop", tag: "Performance", description: "Stability sidewalls and wide base for power days. Great for cross-training and conditioning." },
  { id: "bbb-run-1", name: "BBB Speedform", color: "Blue/White", colors: ["Blue/White", "Grey/Orange"], sizes: [6,7,8,9,10,11], price: 139, rating: 4.2, reviews: 81, image: "https://images.unsplash.com/photo-1543508282-6319a3e2621f?q=80&w=1200&auto=format&fit=crop", tag: "Lightweight", description: "Featherweight runner with energetic foam. Daily miles feel easy and fast." }
];

// --- Cart utils ---
const loadCart = () => { try { const raw = localStorage.getItem("bbb_cart"); return raw ? JSON.parse(raw) : []; } catch { return []; } };
const saveCart = (items) => localStorage.setItem("bbb_cart", JSON.stringify(items));

function useCart() {
  const [items, setItems] = useState(() => loadCart());
  const totalQty = useMemo(() => items.reduce((s,it)=>s+it.qty,0), [items]);
  const subtotal = useMemo(() => items.reduce((s,it)=>s+it.qty*it.price,0), [items]);

  useEffect(()=>{ saveCart(items); }, [items]);

  const add = (product, {size}) => {
    const key = `${product.id}-${size}`;
    setItems(prev=>{
      const idx = prev.findIndex(p=>p.key===key);
      if(idx>=0){ const next=[...prev]; next[idx]={...next[idx], qty: Math.min(10, next[idx].qty+1)}; return next; }
      return [...prev,{ key, id: product.id, name: product.name, size, price: product.price, image: product.image, qty:1 }];
    });
  };
  const remove = (key)=>setItems(prev=>prev.filter(p=>p.key!==key));
  const setQty = (key,qty)=>setItems(prev=>prev.map(p=>(p.key===key?{...p,qty:Math.max(1,Math.min(10,qty))}:p)));
  const clear = ()=>setItems([]);

  return {items, totalQty, subtotal, add, remove, setQty, clear};
}

// --- Badge & Rating ---
const Badge = ({children})=>(<span className="absolute top-2 left-2 bg-black/80 text-white text-xs px-2 py-1 rounded-full">{children}</span>);
const Star = ({filled})=>(<svg viewBox="0 0 24 24" className={`h-4 w-4 ${filled?"fill-yellow-400":"fill-gray-300"}`}><path d="M12 17.27 18.18 21l-1.64-7.03L22 9.24l-7.19-.61L12 2 9.19 8.63 2 9.24l5.46 4.73L5.82 21z"/></svg>);
const Rating = ({value})=>(<div className="flex items-center gap-1" aria-label={`Rating ${value} out of 5`}>{Array.from({length:5}).map((_,i)=><Star key={i} filled={i<Math.round(value)}/> )}</div>);

// --- Navbar ---
function NavBar({cartCount, onOpenCart}) {
  const [mobile,setMobile]=useState(false);

  return (
    <header className="sticky top-0 z-50 bg-white shadow-sm">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 flex items-center justify-between h-16">
        {/* Left spacer */}
        <div className="flex-1" />

        {/* Centered brand */}
        <div className="flex-1 flex justify-center text-2xl sm:text-3xl font-bold">
          Elev8
        </div>

        {/* Right nav links + cart */}
        <div className="flex-1 flex justify-end items-center gap-6">
          <nav className="hidden md:flex items-center gap-6 text-sm font-medium">
            <a href="#shop" className="hover:text-gray-700">Shop</a>
            <a href="#about" className="hover:text-gray-700">About</a>
            <a href="#support" className="hover:text-gray-700">Support</a>
          </nav>
          <button onClick={onOpenCart} className="relative px-3 py-1 border rounded hover:shadow">
            Cart
            {cartCount>0 && <span className="absolute -top-2 -right-2 bg-black text-white text-xs px-2 py-0.5 rounded-full">{cartCount}</span>}
          </button>
          <button className="md:hidden px-3 py-1 border rounded" onClick={()=>setMobile(!mobile)} aria-expanded={mobile}>Menu</button>
        </div>
      </div>

      {/* Mobile menu */}
      {mobile && (
        <div className="md:hidden flex flex-col gap-2 py-2 px-4">
          <a href="#shop" className="px-2 py-1">Shop</a>
          <a href="#about" className="px-2 py-1">About</a>
          <a href="#support" className="px-2 py-1">Support</a>
        </div>
      )}
    </header>
  );
}

// --- Hero ---
function Hero() {
  return (
    <section className="bg-gradient-to-br from-zinc-900 via-black to-zinc-800 text-white">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8 py-16 lg:py-24 grid lg:grid-cols-2 gap-10 items-center">
        <div>
          <h1 className="text-4xl sm:text-5xl font-extrabold">
            Elev8 Your Game
            <span className="block text-zinc-300 font-medium text-xl sm:text-2xl mt-3">Fall '25 Collection</span>
          </h1>
          <p className="mt-6 text-zinc-300 max-w-lg">Step into precision-built sneakers engineered for speed, style, and dominance.</p>
          <div className="mt-6 flex gap-3">
            <a href="#shop" className="rounded-2xl bg-white text-black px-5 py-2 font-semibold hover:bg-zinc-100">Shop now</a>
            <a href="#about" className="rounded-2xl border border-white/30 px-5 py-2 font-semibold hover:bg-white/10">Learn more</a>
          </div>
        </div>
        <motion.div initial={{opacity:0, y:20}} whileInView={{opacity:1, y:0}} transition={{type:"spring", stiffness:80}} className="relative">
          <img src="https://images.unsplash.com/photo-1519741497674-611481863552?q=80&w=1600&auto=format&fit=crop" alt="Hero shoe" className="w-full rounded-3xl shadow-2xl" />
        </motion.div>
      </div>
    </section>
  );
}

// --- ProductCard ---
function ProductCard({product, onAdd}) {
  const [size,setSize] = useState(product.sizes[0]);
  return (
    <div className="border rounded-xl overflow-hidden shadow hover:shadow-lg flex flex-col relative">
      {product.tag && <Badge>{product.tag}</Badge>}
      <img src={product.image} alt={product.name} className="w-full h-32 object-cover" />
      <div className="p-3 flex flex-col gap-1 flex-1">
        <h3 className="font-semibold text-sm">{product.name}</h3>
        <p className="text-xs text-gray-500">{product.reviews} reviews</p>
        <p className="font-bold text-sm">{fmt(product.price)}</p>
        <div className="flex gap-1 overflow-x-auto py-1">
          {product.sizes.map(sz=>(<button key={sz} onClick={()=>setSize(sz)} className={`px-2 py-1 text-xs border rounded ${sz===size?'bg-black text-white':'bg-white text-black'}`}>{sz}</button>))}
        </div>
        <button onClick={()=>onAdd(product,{size})} className="mt-auto w-full bg-black text-white py-1 text-sm rounded hover:bg-gray-900">Add to Cart</button>
      </div>
    </div>
  );
}

// --- Shop ---
function Shop({onAdd}) {
  return (
    <section id="shop" className="py-12 bg-gray-50">
      <div className="mx-auto max-w-7xl px-4 sm:px-6 lg:px-8">
        <h2 className="text-2xl font-bold mb-6">Shop the Collection</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-5 gap-3">
          {CATALOG.map(prod => <ProductCard key={prod.id} product={prod} onAdd={onAdd} />)}
        </div>
      </div>
    </section>
  );
}

// --- CartDrawer ---
function CartDrawer({cart, subtotal, onClose, onRemove, onSetQty, onCheckout}) {
  return (
    <AnimatePresence>
      {cart.length>0 && (
        <motion.div initial={{x:300}} animate={{x:0}} exit={{x:300}} className="fixed top-0 right-0 w-96 h-full bg-white shadow-lg z-50 flex flex-col">
          <div className="flex items-center justify-between p-4 border-b">
            <h2 className="text-lg font-bold">Your Cart</h2>
            <button onClick={onClose} className="text-xl font-bold">×</button>
          </div>
          <div className="flex-1 overflow-y-auto p-4 space-y-4">
            {cart.map(item=>(
              <div key={item.key} className="flex items-center gap-3 border-b pb-2">
                <img src={item.image} alt={item.name} className="w-16 h-16 object-cover rounded" />
                <div className="flex-1">
                  <h3 className="font-semibold text-sm">{item.name}</h3>
                  <p className="text-xs text-gray-500">Size: {item.size}</p>
                  <p className="text-sm font-bold">{fmt(item.price*item.qty)}</p>
                  <div className="flex items-center gap-2 mt-1">
                    <button onClick={()=>onSetQty(item.key,item.qty-1)} className="px-2 border rounded">-</button>
                    <span>{item.qty}</span>
                    <button onClick={()=>onSetQty(item.key,item.qty+1)} className="px-2 border rounded">+</button>
                  </div>
                </div>
                <button onClick={()=>onRemove(item.key)} className="text-red-500 font-bold">×</button>
              </div>
            ))}
          </div>
          <div className="p-4 border-t">
            <p className="text-lg font-bold mb-3">Subtotal: {fmt(subtotal)}</p>
            <button onClick={onCheckout} className="w-full bg-black text-white py-2 rounded-2xl font-semibold hover:bg-gray-900">Checkout</button>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

// --- Main App ---
export default function App() {
  const cart = useCart();
  const [cartOpen,setCartOpen] = useState(false);

  const handleCheckout = ()=>{
    alert("This is a demo. No real payment is processed.");
    cart.clear();
    setCartOpen(false);
  };

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <NavBar cartCount={cart.totalQty} onOpenCart={()=>setCartOpen(true)} />
      <Hero />
      <Shop onAdd={cart.add} />
      <CartDrawer 
        cart={cart.items} 
        subtotal={cart.subtotal} 
        onClose={()=>setCartOpen(false)} 
        onRemove={cart.remove} 
        onSetQty={cart.setQty} 
        onCheckout={handleCheckout} 
      />
    </div>
  );
}
