import React, { useState, useEffect, useMemo } from "react";
import { motion, AnimatePresence } from "framer-motion";

// Generate random shoe catalog
const generateCatalog = () => {
  const names = ["Elev8 Strike", "Air Dominator", "Speed Runner", "Flight Max", "Bounce Fury", "Storm Flex"];
  const tags = ["New", "Hot", "Classic", "Limited", "Best Seller"];
  return Array.from({ length: 16 }, (_, i) => ({
    id: `shoe-${i + 1}`,
    name: names[Math.floor(Math.random() * names.length)],
    price: Math.floor(Math.random() * 100) + 100,
    sizes: [7, 8, 9, 10, 11],
    image: `https://source.unsplash.com/random/400x400?shoe,sneakers&sig=${i + 1}`,
    tag: tags[Math.floor(Math.random() * tags.length)],
  }));
};

const fmt = (n) => `$${n.toFixed(2)}`;

function useCart() {
  const [items, setItems] = useState(() => {
    try {
      const stored = localStorage.getItem("cart");
      return stored ? JSON.parse(stored) : [];
    } catch {
      return [];
    }
  });

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(items));
  }, [items]);

  const totalQty = useMemo(() => items.reduce((sum, i) => sum + i.qty, 0), [items]);
  const subtotal = useMemo(() => items.reduce((sum, i) => sum + i.qty * i.price, 0), [items]);

  const add = (product, { size }) => {
    const key = `${product.id}-${size}`;
    setItems((prev) => {
      const idx = prev.findIndex((p) => p.key === key);
      if (idx >= 0) {
        const next = [...prev];
        next[idx] = { ...next[idx], qty: Math.min(10, next[idx].qty + 1) };
        return next;
      }
      return [...prev, { key, id: product.id, name: product.name, size, price: product.price, image: product.image, qty: 1 }];
    });
  };

  const remove = (key) => setItems((prev) => prev.filter((p) => p.key !== key));
  const clear = () => setItems([]);
  const setQty = (key, qty) =>
    setItems((prev) =>
      prev.map((p) => (p.key === key ? { ...p, qty: Math.max(1, Math.min(10, qty)) } : p))
    );

  return { items, subtotal, totalQty, add, remove, clear, setQty };
}

function NavBar({ cartCount }) {
  return (
    <nav className="fixed top-0 w-full bg-white shadow z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold">Elev8 Store</h1>
        <div className="flex gap-4 items-center">
          <span className="text-sm font-medium">ðŸ›’ {cartCount} items</span>
        </div>
      </div>
    </nav>
  );
}

function ProductCard({ product, onAdd }) {
  const [size, setSize] = useState(product.sizes[0]);

  return (
    <div className="bg-white rounded shadow hover:shadow-lg overflow-hidden flex flex-col text-black">
      <div className="relative">
        <img src={product.image} alt={product.name} className="w-full h-48 object-cover" />
        <span className="absolute top-2 left-2 bg-black text-white text-xs px-2 py-1 rounded-full">{product.tag}</span>
      </div>
      <div className="p-3 flex flex-col flex-1">
        <h3 className="text-sm font-semibold">{product.name}</h3>
        <p className="text-sm">{fmt(product.price)}</p>
        <div className="flex gap-1 mt-1 mb-2">
          {product.sizes.map((sz) => (
            <button
              key={sz}
              onClick={() => setSize(sz)}
              className={`text-xs px-2 py-1 border rounded ${sz === size ? "bg-black text-white" : ""}`}
            >
              {sz}
            </button>
          ))}
        </div>
        <button
          onClick={() => onAdd(product, { size })}
          className="mt-auto bg-black text-white text-sm rounded py-1 hover:bg-gray-800"
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}

function CheckoutSummary({ subtotal, onCheckout }) {
  return (
    <div className="mt-10 text-center">
      <p className="text-xl font-semibold mb-2">Subtotal: {fmt(subtotal)}</p>
      <button
        onClick={onCheckout}
        className="bg-black text-white px-6 py-2 rounded-full hover:bg-gray-800"
      >
        Checkout
      </button>
    </div>
  );
}

export default function App() {
  const cart = useCart();
  const [catalog] = useState(generateCatalog);

  const handleCheckout = () => {
    alert("This is a demo. No real payment is processed.");
    cart.clear();
  };

  return (
    <div className="bg-gray-100 min-h-screen pt-20">
      <NavBar cartCount={cart.totalQty} />
      <main className="max-w-7xl mx-auto px-4 py-6">
        <h2 className="text-2xl font-bold mb-4">Shop All Shoes</h2>
        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
          {catalog.map((product) => (
            <ProductCard key={product.id} product={product} onAdd={cart.add} />
          ))}
        </div>
        <CheckoutSummary subtotal={cart.subtotal} onCheckout={handleCheckout} />
      </main>
    </div>
  );
}
