import React, { useState, useEffect, useMemo } from "react";

// --- Fake product generator ---
const generateShoes = () => {
  const names = ["Flight Max", "Bounce Edge", "Swift Pro", "SkyJet", "Volt Boost", "Ignite Rise"];
  const tags = ["New", "Best Seller", "Limited", "Hot"];
  return Array.from({ length: 16 }, (_, i) => ({
    id: `shoe-${i + 1}`,
    name: names[Math.floor(Math.random() * names.length)],
    price: Math.floor(Math.random() * 100) + 100,
    sizes: [7, 8, 9, 10, 11],
    image: `https://picsum.photos/seed/shoe${i}/400/400`, // âœ… Stable image source
    tag: tags[Math.floor(Math.random() * tags.length)],
  }));
};

const fmt = (n) => `$${n.toFixed(2)}`;

// --- Cart Hook ---
function useCart() {
  const [items, setItems] = useState(() => {
    try {
      const stored = localStorage.getItem("cart");
      return stored ? JSON.parse(stored) : [];
    } catch {
      return [];
    }
  });

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(items));
  }, [items]);

  const totalQty = useMemo(() => items.reduce((sum, i) => sum + i.qty, 0), [items]);
  const subtotal = useMemo(() => items.reduce((sum, i) => sum + i.qty * i.price, 0), [items]);

  const add = (product, { size }) => {
    const key = `${product.id}-${size}`;
    setItems((prev) => {
      const idx = prev.findIndex((p) => p.key === key);
      if (idx >= 0) {
        const next = [...prev];
        next[idx] = { ...next[idx], qty: Math.min(10, next[idx].qty + 1) };
        return next;
      }
      return [...prev, { key, id: product.id, name: product.name, size, price: product.price, image: product.image, qty: 1 }];
    });
  };

  const clear = () => setItems([]);
  return { items, totalQty, subtotal, add, clear };
}

// --- Navbar ---
function NavBar({ cartCount }) {
  return (
    <nav className="bg-white shadow fixed top-0 w-full z-50">
      <div className="max-w-7xl mx-auto px-4 py-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold">Elev8 Store</h1>
        <span className="text-sm font-medium">ðŸ›’ {cartCount} items</span>
      </div>
    </nav>
  );
}

// --- Product Card ---
function ProductCard({ product, onAdd }) {
  const [size, setSize] = useState(product.sizes[0]);
  return (
    <div className="bg-white text-black rounded shadow overflow-hidden flex flex-col">
      <img src={product.image} alt={product.name} className="h-48 w-full object-cover" />
      <div className="p-3 flex flex-col gap-1 flex-1">
        <span className="text-xs bg-gray-200 rounded-full px-2 py-1 w-fit">{product.tag}</span>
        <h3 className="text-sm font-semibold">{product.name}</h3>
        <p className="text-sm">{fmt(product.price)}</p>
        <div className="flex gap-1 overflow-x-auto py-1">
          {product.sizes.map((sz) => (
            <button
              key={sz}
              onClick={() => setSize(sz)}
              className={`px-2 py-1 text-xs border rounded ${sz === size ? "bg-black text-white" : ""}`}
            >
              {sz}
            </button>
          ))}
        </div>
        <button
          onClick={() => onAdd(product, { size })}
          className="mt-auto w-full bg-black text-white py-1 text-sm rounded hover:bg-gray-900"
        >
          Add to Cart
        </button>
      </div>
    </div>
  );
}

// --- Checkout Summary ---
function CheckoutSummary({ subtotal, onCheckout }) {
  return (
    <div className="mt-8 text-center">
      <p className="text-lg font-semibold mb-2">Subtotal: {fmt(subtotal)}</p>
      <button onClick={onCheckout} className="bg-black text-white px-6 py-2 rounded-full hover:bg-gray-800">
        Checkout
      </button>
    </div>
  );
}

// --- App ---
export default function App() {
  const cart = useCart();
  const [catalog] = useState(generateShoes);

  const handleCheckout = () => {
    alert("This is a demo. No real payment is processed.");
    cart.clear();
  };

  return (
    <div className="bg-gray-100 min-h-screen pt-24 px-4">
      <NavBar cartCount={cart.totalQty} />

      <div className="max-w-7xl mx-auto">
        <h2 className="text-2xl font-bold mb-4">Our Collection</h2>

        <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
          {catalog.map((product) => (
            <ProductCard key={product.id} product={product} onAdd={cart.add} />
          ))}
        </div>

        <CheckoutSummary subtotal={cart.subtotal} onCheckout={handleCheckout} />
      </div>
    </div>
  );
}
